import React from "react";
import axios from "axios";
import Layout from "../../components/Layout";
import { connect } from "react-redux";
import ValidateTable from "../../components/ValidateKYBComp";

class QueryView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        companyName: null,
        companyCountry: null,
        LEI: null,
        isNextEnabled: null
      }
    this.handleChange = this.handleChange.bind(this);
    this.stringNotEmpty = this.stringNotEmpty.bind(this);
  }

  static async getInitialProps({ reduxStore, req }) {
    //returned value here is getting mered with the mapstatetoprops
    // mapstatetoprops overrides these values if they match
    return {
      userDetails: req.userDetails,
    };
  }


  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;
    console.log(name + " " + value)
    // setInputs(values => ({...values, [name]: value}))
    this.setState({
        [name]: value
      })
      if (this.state.LEI || (this.state.companyCountry && this.state.companyName)){
          this.setState({
            isNextEnabled: true
          })
      }
  }

  stringNotEmpty(s){
      return s !== null && s!== undefined && s.length > 0 
  }

  render() {
    return (
      <form className="container">
        <label className="row">
          Company Name:
          <input type="text" name="companyName"  onChange={this.handleChange}/>
        </label>
        <label className="row">
          Company Country:
          <input type="text" name="companyCountry" onChange={this.handleChange}/>
        </label>
        <label className="row">
          LEI:
          <input type="text" name="LEI"  onChange={this.handleChange}/>
        </label>
      </form>
    );
  }
}

function mapStateToProps(state) {
  return {
    test: "Test",
  };
}

const mapDispatchToProps = (dispatch) => {
  return {};
};
export default connect(mapStateToProps, mapDispatchToProps)(QueryView);
